function showwin() {

    var winNode = $("#win");
    //winNode.fadeIn("slow"); //淡入淡出
    //winNode.show("slow"); 百叶窗形式
    winNode.css("display","block"); //直接显示窗口
}

function hide() {
    var winNode = $("#win");
    winNode.css("display","none");
    //winNode.hide("slow");
}

